package CodeUp;

import java.util.Scanner;

public class CodeUp1149 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		int b = sc.nextInt();
		
		sc.close();
		
		if(a > b) {
			System.out.println(a);
		}
		else {
			System.out.println(b);	
		}

	}
}
